CREATE PROCEDURE [dbo].[sp_GetProductDetails]
	@Name varchar(300)
AS

BEGIN
	DECLARE @Id varchar(10),@TempId int, @Count int;
	set @Count = (SELECT count(*) FROM Category)+1;
	set @Id = 'CT' + (FORMAT(@Count,'0000'))
	INSERT INTO Category values( @Id , @Name);
END

CREATE PROCEDURE [dbo].[sp_InsertCategory]
	@Name varchar(50)
AS

BEGIN DECLARE @money money = @Price, @TempSubCategoryId varchar(30),@Id varchar(50), @StockId varchar(50),@Count int, @TempPrice nvarchar(50),@ProductCount int set @TempSubCategoryId = (SELECT Id from SubCategory WHERE Name = @SubCategoryName) set @Count = (SELECT count(*) from Product)+1; set @Id = 'P' + @TempSubCategoryId + 'SC' + (FORMAT(@Count,'00000')); set @ProductCount = (SELECT count(1) from Product WHERE Id = @Id) set @Count = (SELECT count(*) from Stock)+1; set @StockId = 'SK' + (FORMAT(@Count,'00000'));
IF(@ProductCount > 0) BEGIN set @TempPrice = (SELECT FORMAT ( @money,'C', 'ta-IN')); INSERT into Product values (@Id,@TempSubCategoryId,@Name,@Description,@Specification,@Options,@TempPrice,@Brand,@IsActive,@Quantity,@ImageURL); INSERT into Stock values (@StockId,@Id,@Quantity); END ELSE BEGIN set @TempPrice = (SELECT FORMAT ( @money,'C', 'ta-IN')); UPDATE Product set SubCategoryId = @TempSubCategoryId, Name = @Name, Description = @Description, Specification = @Specification, Options = @Options, Price = @TempPrice, Brand =@Brand, IsActive = @IsActive, Quantity=@Quantity, ImageURL= @ImageURL WHERE Id = @Id;
UPDATE Stock set ProductId = @Id, Quantity = @Quantity WHERE Id = @StockId; END END

CREATE PROCEDURE [dbo].[sp_InsertProduct]
	@SubCategoryName varchar(50),
	@Name varchar(100),
	@Description nvarchar(MAX),
	@Specification nvarchar(MAX),
	@Options nvarchar(MAX),
	@Price nvarchar(50),
	@Brand varchar(50),
	@IsActive int,
	@Quantity int,
	@ImageURL varchar(200)

AS

BEGIN
	DECLARE @Id varchar(30),@Count int, @TempCategoryId varchar(10);
	set @Count = (select count(*) from SubCategory)+1;
	set @TempCategoryId = (select Id from Category WHERE Name = @CategoryName);
	set @Id = @TempCategoryId + 'SC' + (FORMAT(@Count,'00000'));

	INSERT INTO SubCategory values( @Id , @TempCategoryId, @Name);
END

CREATE PROCEDURE [dbo].[sp_InsertSubCategory]
	@CategoryName varchar(50),
	@Name varchar(100)
AS

BEGIN
	DECLARE @Count int,@Datetime varchar(20),@Id varchar(30)
	set @Count = (select count(*) from Payment)+1;
	set @Id = 'PY' + (FORMAT(@Count,'0000'))
	set @Datetime = (select FORMAT (GETDATE(),'dd/MM/yyyy'))

	INSERT into Payment values(@Id,@CustomerId,@PaymentMode,@AmountPaid,@Datetime);
	SELECT @Id;
END

CREATE PROCEDURE [dbo].[sp_MakePayment]
		@CustomerId varchar(30),
		@PaymentMode varchar(20),
		@AmountPaid nvarchar(30)
AS

BEGIN
	DECLARE @Id varchar(30),@Count int
	set @Count = (select count(*) FROM Orders)+1;
	set @Id = 'ORD' + (FORMAT(@Count,'000000'));

	INSERT into Orders values(@Id,@Quantity,@Price,@DateOrdered,@DateReceived,@Status,@CustomerId,@PaymentId,@ProductId,@AddressId);
END

