	@Specification nvarchar(MAX),
	@Options nvarchar(MAX),
	@Price DECIMAL(10,2),
	@Brand varchar(50),
	@IsActive int,
	@Quantity int,
	@ImageURL varchar(200)

AS

BEGIN
	DECLARE @Id INT,@Count INT;
	set @Id = (select COUNT(*) from SubCategory)+1;
